changeSet.sql:
		delete from user_context_t c where c.role_id=10 and c.user_id < 30;
               insert into user_context_t(user_id, role_id)
                     (select u.user_id, r.role_id from role_t r, user_t u 
                      where r.role_id=10 
                      and u.user_id < 30);
